const storedProducts=JSON.parse(localStorage.getItem("betsyDatabase")).products,productsEl=document.getElementById("products_section");let productsContent="";const updateProducts=storedProducts.forEach(t=>{productsContent+=`\n        <article id="product_${t.productId}" class="product">\n            <section id="info_${t.productId}" class="info">\n                <h1 class="title">${t.title}</h1>\n                <img src="${t.image}" alt="${t.title}">\n                <p class="description">${t.description}</p>\n                <p class="price">Price: ${t.price}</p>\n                <p class="quantity">Number Available: ${t.quantity}</p>\n            </section>\n            <section id="review_${t.productId}" class="review">\n                <h1>Reviews of ${t.title}</h1>\n            </section>\n        </article>\n    `});productsEl.innerHTML+=productsContent,module.exports=updateProducts;