const storedProducts=JSON.parse(localStorage.getItem("productDatabase")).products,productsEl=document.getElementById("products_section");let productsContent="";const updateProducts=storedProducts.forEach(t=>{productsContent+=`\n        <section id="${t.productId}">\n            <h1>${t.title}</h1>\n            <img src="${t.image}" alt="${t.title}">\n            <p>${t.description}</p>\n            <p>${t.price}</p>\n            <p>${t.quantity}</p>\n        </section>\n    `});productsEl.innerHTML+=productsContent,module.exports=updateProducts;