const storedProducts=JSON.parse(localStorage.getItem("betsyDatabase")).products;let productsContent="";const updateProducts=storedProducts.forEach(t=>{productsContent+=`\n        <article id="product_${t.productId}">\n            <h1>${t.title}</h1>\n            <img src="${t.image}" alt="${t.title}">\n            <p>${t.description}</p>\n            <p>Price: ${t.price}</p>\n            <p>Number Available: ${t.quantity}</p>\n            <section class="reviews review_${t.productId}">\n                <p>Reviews of ${t.title}</p>\n            </section>\n        </article>\n    `});module.exports=updateProducts;