const storedReviews=JSON.parse(localStorage.getItem("betsyDatabase")).reviews,updateReviews=storedReviews.forEach(e=>{let t="";const s=`${e.productId}`;if(s){const n="review_"+s,o=document.getElementById(n);t+=`\n            <section class="user_review">\n                <p>${e.review}</p>\n                <p>written by: ${e.author}</p>\n            </section>\n        `,o.innerHTML+=t}});module.exports=updateReviews;