const storedReviews=JSON.parse(localStorage.getItem("betsyDatabase")).reviews,updateReviews=storedReviews.forEach(e=>{let t="";const s=`${e.productId}`;if(s){const o="review_"+s,r=document.getElementById(o);t+=`\n            <section class="user_review">\n                <p>${e.review}</p>\n                <p class="author">written by: ${e.author}</p>\n            </section>\n        `,r.innerHTML+=t}});module.exports=updateReviews;